import {CHANGE_BG, RESET_BG} from './bg.actionType'

export const BG_KEY = 'bgstore'

let initialState = {
    color: "#FFF"
}

let bgReducer = (state=initialState, action) =>{
    let {type,payload} = action
    switch(type){
        case 'CHANGE_BG_ASYNC':
            return{
                //required Color:
                color: "#5E4FDB"
            }
        case 'CHANGE_SECONDARY_BG':
            return{
                //required Color:
                color: "#F3F2FC"
            }
        default: return state
    }
}

export {bgReducer}
