import { put, delay, takeLatest, all, fork } from "redux-saga/effects";


function* chaneBGAsync(){
    yield delay(2000);
    yield put({type: "CHANGE_BG_ASYNC", value:"#000"})
}

function* ageDownAsync(){
    yield delay(2000);
    yield put({type: "AGE_DOWN_ASYNC", value:1})
}

 function* changeBg(){
    yield takeLatest('CHANGE_BG',chaneBGAsync)
}

function* resetBg(){
    yield takeLatest('AGE_DOWN',ageDownAsync)
}


export function* rootSaga () {
    yield all([
        fork(changeBg), // saga1 can also yield [ fork(actionOne), fork(actionTwo) ]
        fork(resetBg),
    ]);
}
