import React from "react";
import { Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

// Redux Imports
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { BG_KEY } from "../redux/bgRedux/bgReducer";
import { changeBG, changeSecondaryBG,changeThirdBG } from "../redux/bgRedux/bg.actions";

const useStyles = makeStyles({
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  primary_button: {
    width: "100px",
    margin: "1em",
    backgroundColor: "#5E4FDB",
    "&:hover": {
      background: "#5E4FDB",
    },
  },
  secondary_button: {
    width: "100px",
    margin: "1em",
    backgroundColor: "#F3F2FC",
    color: "#5E4FDB",
    "&:hover": {
      background: "#F3F2FC",
    },
  },
  white_button: {
    width: "100px",
    margin: "1em",
    backgroundColor: "#FFF",
    color: "#5E4FDB",
    "&:hover": {
      background: "#FFF",
    },
  },
});

const Buttons = () => {
  // const count = useSelector((state) => state.counter.count) /*Store Value*/
  const dispatch = useDispatch();

  //   const handleIncrement = () =>{
  //     dispatch(increment())
  // }

  const styles = useStyles();

  //Dispatchnig Actions

  const changePrimary = () => {
    dispatch(changeBG());
  };
  const changeSecondary = () =>{
    dispatch(changeSecondaryBG())
  }

  const changeThirdButton = () =>{
    dispatch(changeThirdBG())
  }

  // view store
  let viewBg = useSelector((state) => {
    return state[BG_KEY];
  });

  return (
    <div className={styles.container} style={{ backgroundColor: viewBg.color }}>
      <Button
        variant="contained"
        color="primary"
        className={styles.primary_button}
        onClick={changePrimary}
      >
        Button
      </Button>
      <Button
        variant="contained"
        color="primary"
        className={styles.secondary_button}
        onClick={changeSecondary}
      >
        Button
      </Button>
      <Button
        variant="contained"
        color="primary"
        className={styles.white_button}
        onClick={changeThirdButton}
      >
        Button
      </Button>
    </div>
  );
};

export default Buttons;
