import React from "react";
import { Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Switch } from "@material-ui/core";

// Redux Imports
import { useDispatch } from 'react-redux'
import { increment } from '../redux/ducks/counter'
import {useSelector} from 'react-redux'

const useStyles = makeStyles({
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  primary_button: {
    width: "100px",
    margin: "1em",
    backgroundColor: "#5E4FDB",
    '&:hover': {
      background: '#5E4FDB',
  },
  },
  secondary_button: {
    width: "100px",
    margin: "1em",
    backgroundColor: "#F3F2FC",
    color: "#5E4FDB",
    '&:hover': {
      background: '#F3F2FC',
  },
  },
  white_button: {
    width: "100px",
    margin: "1em",
    backgroundColor: "#FFF",
    color: "#5E4FDB",
    '&:hover': {
      background: '#FFF',
  },
  },
  row1_button_1: {
    width: "10px",
    margin: "1em",
    backgroundColor: "#FFF",
    color: "#852121",
    border: "2px solid #852121",
  },
  row1_button_2: {
    width: "10px",
    margin: "1em",
    backgroundColor: "#FFF",
    color: "#5E4FDB",
    border: "2px solid #5E4FDB",
  },
  row1_button_3: {
    width: "10px",
    margin: "1em",
    backgroundColor: "#FFF",
    color: "#FFF",
    border: "2px solid #FFE3E3",
  },
  row2_button_1: {
    width: "10px",
    margin: "1em",
    backgroundColor: "#0E4316",
    color: "#FFF",
  },
  row2_button_2: {
    width: "10px",
    margin: "1em",
    backgroundColor: "#5E4FDB",
    color: "#FFF",
  },
  row2_button_3: {
    width: "10px",
    margin: "1em",
    backgroundColor: "#1ABC9C",
    color: "#FFF",
  },
  swithableButtonsDiv: {
    display: "flex",
    flexDirection: "column",
  },
});

const Buttons = () => {

  const count = useSelector((state) => state.counter.count) /*Store Value*/
  const dispatch = useDispatch();


  const handleIncrement = () =>{
    dispatch(increment())
}

  const styles = useStyles();
  const [state, setState] = React.useState({
    checkedA: true,
  });

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  return (
    <div className={styles.container}>
      <Button
        variant="contained"
        color="primary"
        className={styles.primary_button}
      >
        Button
      </Button>
      <Button
        variant="contained"
        color="primary"
        className={styles.secondary_button}
      >
        Button
      </Button>
      <Button
        variant="contained"
        color="primary"
        className={styles.white_button}
      >
        Button
      </Button>
      <div className={styles.smallButtonsDiv}>
        <div className={styles.row}>
          <Button
            variant="contained"
            color="primary"
            className={styles.row1_button_1}
          >
            -
          </Button>
          <Button
            variant="contained"
            color="primary"
            className={styles.row1_button_2}
          >
            +
          </Button>
          <Button
            variant="contained"
            color="primary"
            className={styles.row1_button_3}
          >
            &#10004;
          </Button>
        </div>
        <div className={styles.row}>
          <Button
            variant="contained"
            color="primary"
            className={styles.row2_button_1}
          >
            -
          </Button>
          <Button
            variant="contained"
            color="primary"
            className={styles.row2_button_2}
          >
            +
          </Button>
          <Button
            variant="contained"
            color="primary"
            className={styles.row2_button_3}
          >
            &#10004;
          </Button>
        </div>
      </div>
      <div className={styles.swithableButtonsDiv}>
        {/* <Switch className={styles.switchButton} size="large"/>
       <Switch className={styles.switchButton} size="large"/>
       <Switch className={styles.switchButton} size="large"/>
       <Switch className={styles.switchButton} size="large"/> */}
        <Switch
          checked={state.checkedA}
          onChange={handleChange}
          name="checkedA"
          inputProps={{ "aria-label": "secondary checkbox" }}
        />
      </div>
      <button onClick={handleIncrement}>+</button>
    </div>
  );
};

export default Buttons;
