import React from "react";
import { Button } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";

// Redux Imports
import { useDispatch } from "react-redux";
import { useSelector } from "react-redux";
import { BG_KEY } from "../redux/bgRedux/bgReducer";
import { changeBG } from "../redux/bgRedux/bg.actions";

const useStyles = makeStyles({
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
  },
  primary_button: {
    width: "100px",
    margin: "1em",
    backgroundColor: "#5E4FDB",
    "&:hover": {
      background: "#5E4FDB",
    },
  },
  secondary_button: {
    width: "100px",
    margin: "1em",
    backgroundColor: "#F3F2FC",
    color: "#5E4FDB",
    "&:hover": {
      background: "#F3F2FC",
    },
  },
  white_button: {
    width: "100px",
    margin: "1em",
    backgroundColor: "#FFF",
    color: "#5E4FDB",
    "&:hover": {
      background: "#FFF",
    },
  },
  swithableButtonsDiv: {
    display: "flex",
    flexDirection: "column",
  },
});

const Buttons = () => {
  // const count = useSelector((state) => state.counter.count) /*Store Value*/
  const dispatch = useDispatch();

  //   const handleIncrement = () =>{
  //     dispatch(increment())
  // }

  const styles = useStyles();
  const [state, setState] = React.useState({
    checkedA: true,
  });

  const handleChange = (event) => {
    setState({ ...state, [event.target.name]: event.target.checked });
  };

  //Dispatchnig Actions

  const changePrimary = () => {
    dispatch(changeBG());
  };

  // view store
  let viewBg = useSelector((state) => {
    return state[BG_KEY];
  });

  return (
    <div className={styles.container} style={{backgroundColor: viewBg.color}}>
      <Button
        variant="contained"
        color="primary"
        className={styles.primary_button}
        onClick={changePrimary}
      >
        Button
      </Button>
      <Button
        variant="contained"
        color="primary"
        className={styles.secondary_button}
      >
        Button
      </Button>
      <Button
        variant="contained"
        color="primary"
        className={styles.white_button}
      >
        Button
      </Button>
      <div className={styles.swithableButtonsDiv}>
        {/* <Switch className={styles.switchButton} size="large"/>
       <Switch className={styles.switchButton} size="large"/>
       <Switch className={styles.switchButton} size="large"/>
       <Switch className={styles.switchButton} size="large"/> */}
        <Switch
          checked={state.checkedA}
          onChange={handleChange}
          name="checkedA"
          inputProps={{ "aria-label": "secondary checkbox" }}
        />
      </div>
      {/* <button onClick={handleIncrement}>+</button> */}
    </div>
  );
};

export default Buttons;
